import React, { useState } from 'react';
import { motion } from 'framer-motion';
import './recruiter.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function RecruiterLogin({ onRecruiterLogin }) {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:2032/checkrecruiterlogin', formData);
      if (response.data != null) {
        onRecruiterLogin();
        localStorage.setItem('recruiter', JSON.stringify(response.data));
        navigate("/recruiterhome");
      } else {
        setMessage("Login Failed");
        setError("");
      }
    } catch (error) {
      setMessage("");
      setError(error.message);
    }
  };

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
      <h3 align="center"><u>Recruiter Login</u></h3>
      {message ? <h4 align="center">{message}</h4> : <h4 align="center" style={{ color: "red" }}>{error}</h4>}
      <motion.form onSubmit={handleSubmit} initial={{ y: -20 }} animate={{ y: 0 }} transition={{ duration: 0.5 }}>
        <div>
          <label>Username</label>
          <input type="text" name="username" value={formData.username} onChange={handleChange} required />
        </div>
        <div>
          <label>Password</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} required />
        </div>
        <motion.button type="submit" className="button" whileHover={{ scale: 1.1 }}>
          Login
        </motion.button>
      </motion.form>
    </motion.div>
  );
}
