import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';

export default function AddJob() {
    const [recruiterData, setRecruiterData] = useState("");

    useEffect(() => {
        const storedRecruiterData = localStorage.getItem('recruiter');
        if (storedRecruiterData) {
            const parsedRecruiterData = JSON.parse(storedRecruiterData);
            setRecruiterData(parsedRecruiterData);
        }
    }, []);

    const [formData, setFormData] = useState({
        title: '',
        description: '',
        company: '',
        roles: [],
        location: '',
        salary: '',
        jobtype: '',
        educationqualifications: '',
        requirements: '',
        email: '',
        deadline: '',
        recruiter: ''
    });

    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData({ ...formData, [id]: value });
    };

    const handleRolesChange = (e) => {
        const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
        setFormData({ ...formData, roles: selectedOptions });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:2032/addjob', { ...formData, recruiter: recruiterData, company: recruiterData.company });
            if (response.status === 200) {
                setFormData({
                    title: '',
                    description: '',
                    company: '',
                    roles: [],
                    location: '',
                    salary: '',
                    jobtype: '',
                    educationqualifications: '',
                    requirements: '',
                    email: '',
                    deadline: '',
                    recruiter: ''
                });
            }
            setMessage(response.data);
            setError("");
        } catch (error) {
            setError(error.response.data);
            setMessage('');
        }
    };

    return (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
            <h3 align="center"><u>Post a New Job</u></h3>
            {message ? <motion.h4 align="center" animate={{ scale: 1.1 }}>{message}</motion.h4> : <motion.h4 align="center" style={{ color: "red" }} animate={{ scale: 1.1 }}>{error}</motion.h4>}
            <motion.form onSubmit={handleSubmit} initial={{ y: 50, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.8 }}>
                {[
                    { label: "Title", id: "title", type: "text" },
                    { label: "Description", id: "description", type: "textarea" },
                    { label: "Location", id: "location", type: "text" },
                    { label: "Salary", id: "salary", type: "number" },
                    { label: "Education Qualifications", id: "educationqualifications", type: "textarea" },
                    { label: "Requirements (Skills)", id: "requirements", type: "textarea" },
                    { label: "Contact Email", id: "email", type: "email" },
                    { label: "Deadline", id: "deadline", type: "date" }
                ].map((field, index) => (
                    <motion.div key={index} whileFocus={{ scale: 1.05 }}>
                        <label>{field.label}</label>
                        {field.type === "textarea" ? (
                            <textarea id={field.id} value={formData[field.id]} onChange={handleChange} required />
                        ) : (
                            <input type={field.type} id={field.id} value={formData[field.id]} onChange={handleChange} required />
                        )}
                    </motion.div>
                ))}
                <motion.button type="submit" whileHover={{ scale: 1.1, backgroundColor: "#4f46e5" }} whileTap={{ scale: 0.9 }}>Post</motion.button>
            </motion.form>
        </motion.div>
    );
}
