import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Extracts parameters from URL
import { motion } from 'framer-motion'; // Import Framer Motion for animations
import './admin.css';

export default function ViewJobSeekerProfile() {
  const [jobseekerData, setJobSeekerData] = useState(null);
  const { email } = useParams(); // Extract 'email' from the URL parameters

  useEffect(() => {
    const fetchJobSeekerData = async () => {
      try {
        const response = await axios.get(`http://localhost:2032/viewjobseekerprofile/${email}`);
        setJobSeekerData(response.data);
      } catch (error) {
        console.error("Error fetching job seeker data:", error.message);
      }
    };

    // Fetch data only if email is present
    if (email) {
      fetchJobSeekerData();
    }
  }, [email]);

  // If email is missing, return null to prevent rendering
  if (!email) {
    return null;
  }

  return (
    <motion.div 
      className="profile-container"
      initial={{ opacity: 0, y: -20 }} // Animation start: invisible, slightly above
      animate={{ opacity: 1, y: 0 }} // Animation end: fully visible, normal position
      transition={{ duration: 0.6 }} // Smooth transition
    >
      {/* Check if jobseekerData exists before rendering */}
      {jobseekerData ? (
        <motion.div 
          className="profile-card"
          initial={{ scale: 0.8, opacity: 0 }} // Start small and invisible
          animate={{ scale: 1, opacity: 1 }} // Expand to normal size with fade-in
          transition={{ duration: 0.5, delay: 0.3 }} // Delayed animation
        >
          <h2>Job Seeker Profile</h2>
          <p><strong>Full Name:</strong> {jobseekerData.fullname}</p>
          <p><strong>Gender:</strong> {jobseekerData.gender}</p>
          <p><strong>Date of Birth:</strong> {jobseekerData.dateofbirth}</p>
          <p><strong>Email:</strong> {jobseekerData.email}</p>
          <p><strong>Location:</strong> {jobseekerData.location}</p>
          <p><strong>Contact:</strong> {jobseekerData.contact}</p>
        </motion.div>
      ) : (
        <motion.p 
          className="error-message"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          No Job Seeker Data Found
        </motion.p>
      )}
    </motion.div>
  );
}
