import React from "react";
import { motion } from "framer-motion";
import "./style.css";

export default function Home() {
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
      className="home-container grid grid-cols-1 md:grid-cols-3 gap-6 p-6"
    >
      {[
        { title: "Admin", items: ["Admin Login", "Change Password", "Add Recruiter", "View Recruiters", "View Job Seekers", "Delete Job Seeker"] },
        { title: "Recruiter", items: ["Recruiter Login", "Post a New Job", "View Posted Jobs", "View Job Applicants", "Change Job Status"] },
        { title: "Job Seeker", items: ["Registration", "Job Seeker Login", "View Profile", "Update Profile", "Apply for a Job", "View Applied Jobs", "Check Job Status"] },
      ].map((section, index) => (
        <motion.div
          key={index}
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8, delay: index * 0.2, ease: "easeOut" }}
          className="bg-white p-6 rounded-2xl shadow-lg hover:shadow-2xl transform hover:scale-105 transition-all duration-300"
        >
          <h3 className="text-xl font-bold text-gray-700 mb-4">{section.title}</h3>
          <ul className="space-y-2 text-gray-600">
            {section.items.map((item, idx) => (
              <motion.li
                key={idx}
                whileHover={{ scale: 1.1, color: "#4f46e5" }}
                className="cursor-pointer"
              >
                {item}
              </motion.li>
            ))}
          </ul>
        </motion.div>
      ))}
    </motion.div>
  );
}
